INSERT INTO `block_custom` VALUES (2,'<div id=\"social-media\">\r\n    <a id=\"facebook\"href=\"#\"><img height=\"50\" width=\"auto\" height=\"50\" width=\"auto\" src=\"/sites/all/themes/rangelands/images/png/facebook.png\"/></a> <a id=\"twitter\" href=\"#\"><img height=\"50\" width=\"auto\" src=\"/sites/all/themes/rangelands/images/png/twitter.png\"/></a> <a id=\"youtube\" href=\"#\"><img height=\"50\" width=\"auto\" src=\"/sites/all/themes/rangelands/images/png/youtube.png\"/></a> <a id=\"flickr\" href=\"#\"><img height=\"50\" width=\"auto\" src=\"/sites/all/themes/rangelands/images/png/flickr.png\"/></a>\r\n</div>','Follow Us','full_html'),(3,'<div id=\"highlight\">\r\n    <h1>International Land Coalition Rangelands Initiative</h1>\r\n\r\n    <p>The ILC Rangelands Initiative is a global programme working to make rangelands more secure for local rangelands users. It contributes to the ILC’s commitment-based initiatives <a href=\"http://www.landcoalition.org/en/commitments/3-diverse-tenure-systems\"  target=\"_blank\">diverse tenure systems</a> and <a href=\"http://www.landcoalition.org/en/commitments/6-locally-managed-ecosystems\" target=\"_blank\">locally-managed ecosystems</a>.</p>\r\n</div>\r\n\r\n<div id=\"about-intro\">\r\n    <div class=\"intro\">\r\n        <p>The ILC Rangelands Initiative works through three key mechanisms to reach its goal of more secure rangelands:</p>\r\n    </div>\r\n    <div class=\"row\">\r\n        <div id=\"connect\" class=\"col-sm-4\">\r\n            <img src=\"/sites/all/themes/rangelands/images/svg/connect.svg\" onerror=\"this.onerror=null; this.src=\'/sites/all/themes/rangelands/images/png/connect.png\'\" width=\"100px\" height=\"auto\">\r\n            <h3>Connecting</h3>\r\n            <p>developing and strengthening partnerships with a common vision of tenure security for local rangelands users</p>\r\n        </div>\r\n        <div id=\"influence\" class=\"col-sm-4\">\r\n            <img src=\"/sites/all/themes/rangelands/images/svg/influence.svg\" onerror=\"this.onerror=null; this.src=\'/sites/all/themes/rangelands/images/png/influence.png\'\" width=\"100px\" height=\"auto\">\r\n            <h3>Influencing</h3>\r\n            <p>sharing experiences and lessons learned with and between different actors in order to influence the development and implementation of supporting policy and legislation</p>\r\n        </div>\r\n        <div id=\"mobilize\" class=\"col-sm-4\">\r\n            <img src=\"/sites/all/themes/rangelands/images/svg/mobilize.svg\" onerror=\"this.onerror=null; this.src=\'/sites/all/themes/rangelands/images/png/mobilize.png\'\" width=\"100px\" height=\"auto\">\r\n            <h3>Mobilizing</h3>\r\n            <p>identifying, developing and supporting innovation solutions and their scaling-up in order to implement enabling policy and legislation</p>\r\n        </div>\r\n    </div>\r\n</div>\r\n\r\n<div id=\"support\">\r\n    <a href=\"https://globalrangelands.org/international-year-rangelands-and-pastoralists-initiative\" target=\"_blank\"><img src=\"/sites/all/themes/rangelands/images/png/grl_logo.png\"/></a>\r\n    <p><a href=\"https://globalrangelands.org/international-year-rangelands-and-pastoralists-initiative\" target=\"_blank\">The ILC Rangelands Initiative supports the call for an <span>International Year of <em>Rangelands</em> and <em>Pastoralists</em></span></a></p>\r\n</div>','The introduction text on the front page','full_html'),(4,'<div id=\"contact-info\">\r\n    <div class=\"row\">\r\n        <div class=\"col-md-3\">\r\n            <address>\r\n                <h4>Rangelands Initiative Global</h4>\r\n                <ul>\r\n                    <li class=\"name\">Ms Fiona Flintan</li>\r\n                    <li class=\"role\">Coordinator</li>\r\n                    <li>International Livestock Research Institute</li>\r\n                    <li>Addis Ababa, Ethiopia</li>\r\n                    <li>f.flintan@cgiar.org</li>\r\n                </ul>\r\n            </address>\r\n        </div>\r\n        <div class=\"col-md-3\">\r\n            <address>\r\n                <h4>Rangelands Initiative Asia</h4>\r\n                <ul>\r\n                    <li class=\"name\">Dr. H. Ykhanbai Jasil</li>\r\n                    <li class=\"role\">Coordinator for Central Asia</li>\r\n                    <li>Ulaanbaatar, Mongolia</li>\r\n                    <li>jasil_ngo05@yahoo.com</li>\r\n                    <li>+976-11-329619</li>\r\n                </ul>\r\n            </address>\r\n        </div>\r\n        <div class=\"col-md-3\">\r\n            <address>\r\n                <h4>Rangelands Initiative Africa</h4>\r\n                <ul>\r\n                    <li class=\"name\">Mr Ken Otieno Reconcile</li>\r\n                    <li class=\"role\">Coordinator</li>\r\n                    <li>Nakuru, Kenya</li>\r\n                    <li>kenotieno@reconcile.ea.org</li>\r\n                </ul>\r\n            </address>\r\n        </div>\r\n        <div class=\"col-md-3\">\r\n            <address>\r\n                <h4>Semi-arids Platform</h4>\r\n                <ul>\r\n                    <li class=\"name\">Mr Gabriel Seghezzo</li>\r\n                    <li class=\"role\">Coordinator</li>\r\n                    <li>Fundapaz, Argentina</li>\r\n                </ul>\r\n            </address>\r\n        </div>\r\n    </div>\r\n    <p>For a general enquiry please the form below to send us a message</p>\r\n</div>','Contact Information','full_html');
