INSERT INTO `cache_filter` VALUES ('full_html::1d857dcdb939488c15e149c0f026cfa6a6d939eaa31b5a3bfa6ad6196d08a28c','<div id=\"highlight\">\n<h1>International Land Coalition Rangelands Initiative</h1>\n<p>The ILC Rangelands Initiative is a global programme working to make rangelands more secure for local rangelands users. It contributes to the ILC’s commitment-based initiatives <a href=\"http://www.landcoalition.org/en/commitments/3-diverse-tenure-systems\" target=\"_blank\">diverse tenure systems</a> and <a href=\"http://www.landcoalition.org/en/commitments/6-locally-managed-ecosystems\" target=\"_blank\">locally-managed ecosystems</a>.</p>\n</div>\n<div id=\"about-intro\">\n<div class=\"intro\">\n<p>The ILC Rangelands Initiative works through three key mechanisms to reach its goal of more secure rangelands:</p>\n</div>\n<div class=\"row\">\n<div id=\"connect\" class=\"col-sm-4\">\n            <img src=\"/sites/all/themes/rangelands/images/svg/connect.svg\" onerror=\"this.onerror=null; this.src=\'/sites/all/themes/rangelands/images/png/connect.png\'\" width=\"100px\" height=\"auto\" /><h3>Connecting</h3>\n<p>developing and strengthening partnerships with a common vision of tenure security for local rangelands users</p>\n</div>\n<div id=\"influence\" class=\"col-sm-4\">\n            <img src=\"/sites/all/themes/rangelands/images/svg/influence.svg\" onerror=\"this.onerror=null; this.src=\'/sites/all/themes/rangelands/images/png/influence.png\'\" width=\"100px\" height=\"auto\" /><h3>Influencing</h3>\n<p>sharing experiences and lessons learned with and between different actors in order to influence the development and implementation of supporting policy and legislation</p>\n</div>\n<div id=\"mobilize\" class=\"col-sm-4\">\n            <img src=\"/sites/all/themes/rangelands/images/svg/mobilize.svg\" onerror=\"this.onerror=null; this.src=\'/sites/all/themes/rangelands/images/png/mobilize.png\'\" width=\"100px\" height=\"auto\" /><h3>Mobilizing</h3>\n<p>identifying, developing and supporting innovation solutions and their scaling-up in order to implement enabling policy and legislation</p>\n</div>\n</div>\n</div>\n<div id=\"support\">\n    <a href=\"https://globalrangelands.org/international-year-rangelands-and-pastoralists-initiative\" target=\"_blank\"><img src=\"/sites/all/themes/rangelands/images/png/grl_logo.png\" /></a>\n<p><a href=\"https://globalrangelands.org/international-year-rangelands-and-pastoralists-initiative\" target=\"_blank\">The ILC Rangelands Initiative supports the call for an <span>International Year of <em>Rangelands</em> and <em>Pastoralists</em></span></a></p>\n</div>\n',0,1508600500,0),('full_html::2c38f132390553458fff169d1270084a08a04614ab98cc06a8c10d36d21e5612','<div id=\"social-media\">\n    <a id=\"facebook\" href=\"#\"><?xml version=\"1.0\" encoding=\"UTF-8\"??><br /><svg width=\"74px\" height=\"74px\" viewbox=\"0 0 74 74\" version=\"1.1\" xmlns=\"http://www.w3.org/2000/svg\" xmlns:xlink=\"http://www.w3.org/1999/xlink\"><!-- Generator: Sketch 47.1 (45422) - http://www.bohemiancoding.com/sketch --><p></p><title>Untitled</title><desc>Created with Sketch.</desc><defs></defs><g id=\"Page-1\" stroke=\"none\" stroke-width=\"1\" fill=\"none\" fill-rule=\"evenodd\"><g id=\"facebook\" fill-rule=\"nonzero\"><circle id=\"Oval\" fill=\"#3B5998\" cx=\"37\" cy=\"37\" r=\"37\"></circle><br /><path d=\"M46.1140808,38.4528093 L39.5886571,38.4528093 L39.5886571,63 L29.702086,63 L29.702086,38.4528093 L25,38.4528093 L25,29.8259498 L29.702086,29.8259498 L29.702086,24.2433917 C29.702086,20.2512602 31.5488918,14 39.6766623,14 L47,14.0314605 L47,22.4052976 L41.6864407,22.4052976 C40.8148631,22.4052976 39.589309,22.8524377 39.589309,24.7567996 L39.589309,29.8339822 L46.9778357,29.8339822 L46.1140808,38.4528093 Z\" id=\"Shape\" fill=\"#FFFFFF\"></path></g></g></svg></a> <a id=\"twitter\" href=\"#\"><i class=\"fa fa-twitter-square fa-3x\"></i></a> <a id=\"youtube\" href=\"#\"><i class=\"fa fa-youtube-square fa-3x\"></i></a> <a i